                ->setParameter('gestion', $gestion)
                ->setParameter('nivel', $nivel)
                ->setParameter('grado', $grado)
                ->setParameter('paralelo', $paralelo)
                ->orderBy('e.paterno', 'ASC')
        ;
        return $qb->getQuery()->getResult();
